mapper.select:
SELECT IFNULL(T1.MD_IDX , '') AS MD_IDX /*모델idx*/ , IFNULL(T1.MODEL_NAME , '') AS MODEL_NAME /*모델명*/ , IFNULL(T1.MODEL_DETAIL_NAME , '') AS MODEL_DETAIL_NAME /*모델상세명*/ , IFNULL(T1.MANUFACTURER_CODE , '') AS MANUFACTURER_CODE /*제조사code*/ , IFNULL(GET_COMMON_CODE_VALUE('CR', T1.MANUFACTURER_CODE) , '' ) AS MANUFACTURER_NAME /*제조사명*/ , IFNULL(T1.COUNTRY_CODE , '') AS COUNTRY_CODE /*국가code*/ , IFNULL(GET_COMMON_CODE_VALUE('CN',T1.COUNTRY_CODE) , '' ) AS CONTURY_NAME /*국가명*/ , IFNULL(T1.IMPORT_CODE , '') AS IMPORT_CODE /*국내 해외구분*/ , IFNULL(GET_COMMON_CODE_VALUE('IM',T1.IMPORT_CODE) , '' ) AS IMPORT_NAME /*국내 해외 명*/ , IFNULL(T1.CARTYPE_CODE , '') AS CARTYPE_CODE /*차종 코드*/ , IFNULL(GET_COMMON_CODE_VALUE('CTY',T1.CARTYPE_CODE) , '' ) AS CARTYPE_NAME /*차종명*/ , IFNULL(T1.FUEL_CODE , '') AS FUEL_CODE /*연료code*/ , IFNULL(GET_COMMON_CODE_VALUE('FL', T1.FUEL_CODE) , '') AS FUEL_NAME /*연료명*/ , IFNULL(T1.TRANSMISSION_CODE , '') AS TRANSMISSION_CODE /*변속기구분code*/ , IFNULL(GET_COMMON_CODE_VALUE('TM', T1.TRANSMISSION_CODE) , '') AS TRANSMISSION_NAME /*변속기명*/ , IFNULL(T1.DRIVE_TYPE_CODE , '') AS DRIVE_TYPE_CODE /*구동방식구분code*/ , IFNULL(GET_COMMON_CODE_VALUE('TM', T1.DRIVE_TYPE_CODE) , '') AS DRIVE_TYPE_NAME /*구동방식구분명*/ , IFNULL(T1.DRIVE_LICENSE_CODE , '') AS DRIVE_LICENSE_CODE /*면허구분code*/ , IFNULL(GET_COMMON_CODE_VALUE('DL', T1.DRIVE_LICENSE_CODE) , '') AS DRIVE_LICENSE_NAME /*면허구분 이름*/ , IFNULL(T1.MAXIMUM_PASSENGER , '') AS MAXIMUM_PASSENGER /*승차인원*/ , IFNULL(T1.DISPLACEMENT , '') AS DISPLACEMENT /*배기량*/ , IFNULL(T1.YEAR , '') AS YEAR /*연식*/ , IFNULL(T1.COLOR_NAME , '') AS COLOR_NAME /*색상*/ , IFNULL(T1.PY_IDX , '') AS PY_IDX /*요금IDX*/ , IFNULL(T1.REG_ID , '') AS REG_ID /*등록자*/ , IFNULL(T1.REG_DT , '') AS REG_DT /*등록일시*/ , IFNULL(T1.MOD_ID , '') AS MOD_ID /*수정자*/ , IFNULL(T1.MOD_DT , '') AS MOD_DT /*수정일시*/ , IFNULL(T1.DEL_YN , '') AS DEL_YN /*삭제여부*/ , IFNULL(T1.IMG_IDX , '') AS IMG_IDX /* 이미지 등록 여부 */ FROM CDT_CAR_MODEL T1
T1.MD_IDX = #{mdIdx}
, T1.MANUFACTURER_CODE = #{manufacturerCode}

SELECT DISTINCT IFNULL(T1.MODEL_NAME , '') AS MODEL_NAME /*모델명*/ FROM CDT_CAR_MODEL T1
T1.MODEL_NAME = #{modelName}
, T1.MANUFACTURER_CODE = #{manufacturerCode}
AND T1.MODEL_NAME IS NOT NULL

mapper.update:
UPDATE CDT_CAR_MODEL
MODEL_NAME = #{modelName} , MODEL_DETAIL_NAME = #{modelDetailName} , MANUFACTURER_CODE = #{manufacturerCode} , COUNTRY_CODE = #{countryCode} , IMPORT_CODE = #{importCode} , CARTYPE_CODE = #{cartypeCode} , FUEL_CODE = #{fuelCode} , TRANSMISSION_CODE = #{transmissionCode} , DRIVE_TYPE_CODE = #{driveTypeCode} , DRIVE_LICENSE_CODE = #{driveLicenseCode} , DISPLACEMENT = #{displacement} , MAXIMUM_PASSENGER = #{maximumPassenger} , DEL_YN = #{delYn} , MOD_ID = #{modId} , MOD_DT = SYSDATE
MD_IDX = #{mdIdx}

mapper.insert:

  INSERT INTO CDT_CAR_MODEL (
      MODEL_NAME
   ,  MODEL_DETAIL_NAME
   ,  MANUFACTURER_CODE
   ,  COUNTRY_CODE
   ,  IMPORT_CODE
   ,  CARTYPE_CODE
   ,  FUEL_CODE
   ,  TRANSMISSION_CODE
   ,  DRIVE_TYPE_CODE
   ,  DRIVE_LICENSE_CODE
   ,  DISPLACEMENT
   ,  MAXIMUM_PASSENGER
   ,  DEL_YN
   ,  REG_ID
   ,  REG_DT
) VALUES (
      #{modelName}
   ,     #{modelDetailName}
   ,     #{manufacturerCode}
   ,     #{countryCode}
   ,     #{importCode}
   ,     #{cartypeCode}
   ,     #{fuelCode}
   ,     #{transmissionCode}
   ,     #{driveTypeCode}
   ,     #{driveLicenseCode}
   ,     #{displacement}
   ,     #{maximumPassenger}
   ,   'N'
   ,     #{regId}
   ,   SYSDATE
)
