		,  INSURANCE_COMPANYMENT3
		,  CAR_DAMAGE_COVER4
		,  INSURANCE_COMPANYMENT4
		,  CAR_DAMAGE1_YN
		,  CAR_DAMAGE2_YN
		,  CAR_DAMAGE3_YN
		,  CAR_DAMAGE4_YN
		,  CI_ETC
		,  DEL_YN
