mapper.select:
SELECT * FROM ( SELECT ROWNUM AS ROW_NUMBER, COUNT(*) OVER() TOTAL_ROW_COUNT, A.* FROM ( SELECT IFNULL(T2.COMPANY_NAME, '') AS COMPANY_NAME /* 회원사명 */ , IFNULL(T2.BRANCH_NAME, '') AS BRANCH_NAME /* 지점명 */ , IFNULL(T1.PER_IDX , '') AS PER_IDX /*기간요금idx*/ , IFNULL(T1.CR_IDX , '') AS CR_IDX /*차량idx*/ , IFNULL(T1.RT_IDX , '') AS RT_IDX /*회원사idx*/ , IFNULL(T1.PERIOD_START_DT , '') AS PERIOD_START_DT /*기간시작일*/ , IFNULL(T1.PERIOD_END_DT , '') AS PERIOD_END_DT /*기간종료일*/ , IFNULL(T1.DISCOUNT_EXTRACHARGE_CODE , '') AS DISCOUNT_EXTRACHARGE_CODE /*할인할증구분*/ , IFNULL(T1.PERIOD_PAY , '') AS PERIOD_PAY /*기간요금*/ , IFNULL(T1.PERIOD_ETC , '') AS PERIOD_ETC /*내용*/ , IFNULL(T1.REG_ID , '') AS REG_ID /*등록자*/ , IFNULL(T1.REG_DT , '') AS REG_DT /*등록일시*/ , IFNULL(T1.MOD_ID , '') AS MOD_ID /*수정자*/ , IFNULL(T1.MOD_DT , '') AS MOD_DT /*수정일시*/ , IFNULL(T1.DEL_YN , '') AS DEL_YN /*삭제여부*/ , (SELECT COUNT(CR_IDX) FROM DC_CAR_INFO WHERE PER_IDX = T1.PER_IDX) AS SETTING_CAR_CNT /* 설정차량대수 */ FROM DC_PAYMENT_PERIOD T1 INNER JOIN DC_RENT_COMPANY T2 ON T1.RT_IDX = T2.RT_IDX ) A
1=1
AND A.RT_IDX = #{rtIdx}
AND A.COMPANY_NAME LIKE '%' || #{searchKeyWord} || '%'
AND A.BRANCH_NAME LIKE '%' || #{searchKeyWord} || '%'
AND A.PER_IDX LIKE '%' || #{searchKeyWord} || '%'
AND A.PERIOD_ETC LIKE '%' || #{searchKeyWord} || '%'
) X
ROWNUM <= #{displayPageNum} AND ROW_NUMBER > (#{page}-1) * #{displayPageNum}

SELECT
IFNULL(T2.COMPANY_NAME, '') AS COMPANY_NAME /* 회원사명 */ , IFNULL(T2.BRANCH_NAME, '') AS BRANCH_NAME /* 지점명 */ , IFNULL(T1.PER_IDX , '') AS PER_IDX /*기간요금idx*/ , IFNULL(T1.CR_IDX , '') AS CR_IDX /*차량idx*/ , IFNULL(T1.RT_IDX , '') AS RT_IDX /*회원사idx*/ , IFNULL(T1.PERIOD_START_DT , '') AS PERIOD_START_DT /*기간시작일*/ , IFNULL(T1.PERIOD_END_DT , '') AS PERIOD_END_DT /*기간종료일*/ , IFNULL(T1.DISCOUNT_EXTRACHARGE_CODE , '') AS DISCOUNT_EXTRACHARGE_CODE /*할인할증구분*/ , IFNULL(T1.PERIOD_PAY , '') AS PERIOD_PAY /*기간요금*/ , IFNULL(T1.PERIOD_ETC , '') AS PERIOD_ETC /*내용*/ , IFNULL(T1.REG_ID , '') AS REG_ID /*등록자*/ , IFNULL(T1.REG_DT , '') AS REG_DT /*등록일시*/ , IFNULL(T1.MOD_ID , '') AS MOD_ID /*수정자*/ , IFNULL(T1.MOD_DT , '') AS MOD_DT /*수정일시*/ , IFNULL(T1.DEL_YN , '') AS DEL_YN /*삭제여부*/ , IFNULL(T1.PERIOD_ETC , '') AS PERIOD_ETC /*내용*/ FROM DC_PAYMENT_PERIOD T1 INNER JOIN DC_RENT_COMPANY T2 ON T1.RT_IDX = T2.RT_IDX
T1.RT_IDX = #{rtIdx}
AND T1.PER_IDX = #{perIdx}

mapper.update:
    	 UPDATE DC_PAYMENT_PERIOD 

    		    PER_IDX					= #{perIdx}  					 
			  , RT_IDX					= #{rtIdx}  					 
			  , PERIOD_START_DT			= #{periodStartDt}  			 
			  , PERIOD_END_DT				= #{periodEndDt}  				 
			  , DISCOUNT_EXTRACHARGE_CODE	= #{discountExtrachargeCode}	 
 			  , PERIOD_PAY				= #{periodPay}  				 
 			  , PERIOD_ETC				= #{periodEtc} 					 
			  , DEL_YN 					= #{delYn}				  	 	 
 			  , MOD_ID 					= #{modId}  				 	 
 			, MOD_DT = SYSDATE



	     		 PER_IDX = #{perIdx}

mapper.insert:


     INSERT INTO DC_PAYMENT_PERIOD (
     	   RT_IDX
		,  PERIOD_ETC
		,  PERIOD_START_DT
		,  PERIOD_END_DT
		,  DISCOUNT_EXTRACHARGE_CODE
		,  PERIOD_PAY
		,  DEL_YN
		,  REG_ID
		,  REG_DT
	) VALUES (
			#{rtIdx}
		,	#{periodEtc}              
		,  	#{periodStartDt}    
		,  	#{periodEndDt}
		,  	#{discountExtrachargeCode}
		,  	#{periodPay}
		,  	#{delYn}
		,  	#{regId}
		,	SYSDATE			
	)
