mapper.select:
SELECT COUNT(*) FROM ( SELECT PS_IDX,PL.RM_IDX, RT_IDX FROM( SELECT PS_IDX, RM_IDX FROM DC_PAYMENT_SCHEDULE WHERE PAYMENT_STATUS_CODE IN ('paid', 'ready') GROUP BY RM_IDX ) PS LEFT OUTER JOIN DC_PAYMENT_LOG PL ON PS.RM_IDX = PL.RM_IDX AND DATE_FORMAT(PL.PAYMENT_DATE, '%Y-%m') = DATE_FORMAT(NOW(), '%Y-%m') JOIN DC_RESERVE_MASTER RM ON PS.RM_IDX = RM.RM_IDX
AND RT_IDX like #{rtIdx}
GROUP BY PL.RM_IDX ) M

SELECT SUM(M.PAYMENT_AMOUNT) FROM ( SELECT PL.PAYMENT_AMOUNT FROM ( SELECT PS_IDX, RM_IDX, PAYMENT_AMOUNT FROM DC_PAYMENT_SCHEDULE WHERE PAYMENT_STATUS_CODE IN ('paid', 'ready') GROUP BY RM_IDX ) PS LEFT OUTER JOIN DC_PAYMENT_LOG PL ON PS.RM_IDX = PL.RM_IDX AND DATE_FORMAT(PL.PAYMENT_DATE, '%Y-%m') = DATE_FORMAT(NOW(), '%Y-%m') JOIN DC_RESERVE_MASTER RM ON PS.RM_IDX = RM.RM_IDX
AND RM.RT_IDX = #{rtIdx}
GROUP BY PL.RM_IDX ) M


SELECT IFNULL(COUNT(RM_IDX)  , 0) as cancelCount
FROM   DC_RESERVE_MASTER
WHERE  RESERVE_STATUS_CODE = '취소'
AND    DATE_FORMAT(REG_DT , '%Y-%m-%d') = #{startTime} 

	 AND RT_IDX = #{rtIdx}
