mapper.select:
SELECT IFNULL(T1.UR_IDX , '' ) AS UR_IDX /* 회원IDX */ , IFNULL(T1.USER_ROLE , '' ) AS USER_ROLE /* 멤버역할(ROLE) */ , IFNULL(T1.USER_NAME , '' ) AS USER_NAME /* 회원이름 */ , IFNULL(T1.RT_IDX , '' ) AS RT_IDX /* 회원이 속해있는 회원사 IDX */ , IFNULL(T2.COMPANY_NAME , '' ) AS COMPANY_NAME /* 회원이 속해있는 회원사 명 */ , IFNULL(T2.BRANCH_NAME , '' ) AS BRANCH_NAME /* 회원이 속해있는 회원사의 지점명 */ , IFNULL(T2.ACCESS_YN , '' ) AS ACCESS_YN /* 접속 YN */ FROM DC_USER_INFO T1 LEFT JOIN DC_RENT_COMPANY T2 ON T1.RT_IDX IS NOT NULL AND T1.RT_IDX = T2.RT_IDX
USER_ID = #{userId}
AND USER_PASSWORD = #{userPassword}
