mapper.select:
SELECT IFNULL(T1.LICENSE_CODE , '' ) AS LICENSE_CODE /*면허종류CODE*/ , IFNULL(T1.LICENSE_EXPIRATION , '' ) AS LICENSE_EXPIRATION /*갱신기간*/ , IFNULL(T1.LICENSE_ISSUE_DT , '' ) AS LICENSE_ISSUE_DT /*발급일*/ , IFNULL(T1.LICENSE_LOCATION , '' ) AS LICENSE_LOCATION /*면허지역*/ , IFNULL(T1.LICENSE_NUMBER , '' ) AS LICENSE_NUMBER /*면허번호*/ , IFNULL(T1.LICENSE_OWNER_NAME , '' ) AS LICENSE_OWNER_NAME /*면허소유자명*/ , IFNULL(T1.MOD_DT , '' ) AS MOD_DT /*수정일시*/ , IFNULL(T1.MOD_ID , '' ) AS MOD_ID /*수정자*/ , IFNULL(T1.REG_DT , '' ) AS REG_DT /*등록일시*/ , IFNULL(T1.REG_ID , '' ) AS REG_ID /*등록자*/ , IFNULL(T1.UL_IDX , '' ) AS UL_IDX /*면허idx*/ , IFNULL(T1.UR_IDX , '' ) AS UR_IDX /*회원iDX*/ , IFNULL(T1.USE_YN , '' ) AS USE_YN /*사용여부*/ FROM CDT_USER_LICENSE_INFO T1
WhERE
T1.UR_IDX = #{urIdx}

